                diffCalculation.lines.push(['dmp-line-compare-equal', "" + diffCalculation.lineLeft, "" + diffCalculation.lineRight, line]);
                diffCalculation.lineLeft++;
                diffCalculation.lineRight++;
            }
        }
        catch (e_1_1) { e_1 = { error: e_1_1 }; }
